pragma solidity 0.5.15;

import "./Governance_ds.sol";

/**
 * @title Governance contract for Tokenz STO platform v0.1
 * @dev A base contract to enforce Governance on entire system
 * @author Harsh Patel / harsh.patel54@gmail.com
 */
contract Governance is Governance_ds{


    modifier mod_isValidPerm(bytes32 objid){
        require(permissionCheck(subject[msg.sender],object[objid]));
        _;
    }

    constructor()
        public
    {
        subject[msg.sender] = 0x0FFFFFFFFFFFFFFF;
        contractRegis[address(this)] = contractDetails(0x0000000000000001,"Governance");
    }

    /**
        @notice permissionCheck Checks permission of subject and object
        @dev This is function pure and public with no modifiers but other modifiers use this function
        @param _sub The bytes representing subject
        @param _obj The bytes representing object
        @return {
          "result": "Returns true if permission match"
        }
    */
    function permissionCheck(bytes8 _sub, bytes8 _obj) public pure returns ( bool  result ) {
        if ( ( (_sub & _obj )^_obj ) == 0) {
            result = true;
        }
    }

    /**
        @notice ruleCheck Function to check the ruleEngine in governance Contract
        @dev This is function public with view rights
        @param from The contractCode representing the senders contracts
        @return {
          "result": "Returns true if function executed successfully"
        }
    */
    function ruleCheck( address from )
        view
        public
        returns ( bool result )
    {
        result = ruleEngine[contractRegis[from].contractCode][contractRegis[msg.sender].contractCode];
    }

    /**
        @notice addPermissionSubject Function to alot permission to subject
        @dev This is function public with modifier mod_isValidPerm
        @param sub The address of the subject
        @param perm The bytes representing subject
        @return {
          "result": "Returns true if function executed successfully"
        }
    */
    function addPermissionSubject ( address sub , bytes8 perm )
        public
        mod_isValidPerm(0xe2f77eda07301dcdfd75d8cb122d00e11c3c76cd1d95474354a43f0a6136e1aa)
        returns ( bool result )
    {
        emit eventsubject(sub,perm);
        subject[sub] = perm;
        result = true;
    }

    /**
        @notice addPermissionObject Function to alot permission to object
        @dev This is function public with modifier mod_isValidPerm
        @param obj The bytes reprresenting the object
        @param perm The bytes representing object
        @return {
          "result": "Returns true if function executed successfully"
        }
    */
    function addPermissionObject ( bytes32 obj,bytes8 perm )
        public
        mod_isValidPerm(0xe6a6bdf4f80c25a08614ac1cc38f8a6866094aaae2eb1386d824a04b67b1a9f4)
        returns ( bool result)
    {
        emit eventobject(obj,perm);
        object[obj] = perm;
        result = true;
    }

    /**
        @notice enableRule Function to enables rules in governance Contract
        @dev This is function public with modifier mod_isValidPerm
        @param from The contractCode representing the senders contracts
        @param to The contractCode representing the receivers contracts
        @return {
          "result": "Returns true if function executed successfully"
        }
    */
    function enableRule( bytes8 from , bytes8 to )
        mod_isValidPerm(0x60e4bc6916f055bd8ec78b0fb09acc593683868e7489071f28e97f17a67023c2)
        public
        returns ( bool result )
    {
        emit enableRule_evt( from , to );
        ruleEngine[from][to] = true;
        result = true;
    }

    /**
        @notice disableRule Function to disable rules in governance Contract
        @dev This is function public with modifier mod_isValidPerm
        @param from The contractCode representing the senders contracts
        @param to The contractCode representing the receivers contracts
        @return {
          "result": "Returns true if function executed successfully"
        }
    */
    function disableRule( bytes8 from , bytes8 to )
        mod_isValidPerm(0xa48594fcd0c5e85901eb04704a60bdec055e99ace219c1bc71cc57071daae99c)
        public
        returns ( bool result )
    {
        emit disableRule_evt( from , to );
        ruleEngine[from][to] = false;
        result = true;
    }

    /**
        @notice registerContract Function to check the ruleEngine in governance Contract
        @dev This is function public with view privilage
        @param _addr The address of the Contract created
        @param _code The contract Code of the contract created.
        @param _name The contract Name of the contract created.
        @return {
          "result": "Returns true if function executed successfully"
        }
    */
    function registerContract( address _addr , bytes8 _code , string memory _name )
        mod_isValidPerm(0xabb38c2df84dd6a2602aab61ef7d43dabe1e3019f91b3b535bdd69201357f3b6)
        public
        returns ( bool result )
    {
        emit registerContract_evt(_addr , _code , _name );
        contractRegis[_addr] = contractDetails( _code , _name );
        result = true;
    }

}

